  export const instantQuoteList = (data) => (dispatch) => {
    dispatch({ type: NEW_ORDER_INSTANT_QUOTE, payload: data });
    localStorage.setItem('shippingAddress', JSON.stringify(data));
  };

export const vehicleDetailsList = (vehicleMake, vehicleModel, vehichleYear) => {
  return {
    type: NEW_ORDER_FORM_VEHICLE_DETAILS,
    payload: {
      vehicleMake,
      vehicleModel,
      vehichleYear,
    },
  };
};
