import { createStore, compose, applyMiddleware, combineReducers } from 'redux';
import thunk from 'redux-thunk';
import { carMakeListReducer, carModelListReducer, carSizeListReducer, carYearListReducer } from './reducers/carReducers';
import { zipCodeFromReducer, zipCodeToReducer } from './reducers/zipCodeReducers';
import { orderFormInformationDetailsReducer, orderFormInstantQuoteReducer, orderFormVehicleDetailsReducer } from './reducers/orderFormReducers'

const initialState = {
  instantQuote: localStorage.getItem('instantQuote')
  ? JSON.parse(localStorage.getItem('instantQuote'))
  : {},
  };
const reducer = combineReducers({
    carMakeList: carMakeListReducer,
    carModelList: carModelListReducer,
    carYearList: carYearListReducer,
    carSizeList: carSizeListReducer,
    zipCodeFromList: zipCodeFromReducer,
    zipCodeToList: zipCodeToReducer,
    orderVehicleDetails: orderFormVehicleDetailsReducer,
    orderInformationDetails: orderFormInformationDetailsReducer,
    orderInstantQuote: orderFormInstantQuoteReducer,

})

const composeEnhancer = window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ || compose;

const store = createStore(reducer, initialState, composeEnhancer(applyMiddleware(thunk)))

export default store;
