import React, { useEffect, useState } from "react";
import { Button, Col, Form, Row } from "react-bootstrap";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import { useDispatch, useSelector } from "react-redux";
import { makeCars, modelCars, yearCars } from "../actions/carActions";
import { vehicleDetailsList } from "../actions/orderFormActions";
import { calculateCost } from "../actions/orderFormActions";

export default function VehicleDetailsForm(props) {
  const dispatch = useDispatch();
  // const { handleChange, checkoutForm } = props;

  const orderInstantQuote = useSelector((state) => state.orderInstantQuote);
  const { instantQuoteDetails } = orderInstantQuote;

  const carMakeList = useSelector((state) => state.carMakeList);
  const carModelList = useSelector((state) => state.carModelList);
  const carYearList = useSelector((state) => state.carYearList);

  const { loading: makeLoading, error: makeError, carMakes } = carMakeList;
  const { loading: modelLoading, error: modelError, carModels } = carModelList;
  const { loading: yearLoading, error: yearError, carYears } = carYearList;

  const [inputs, setInputs] = useState({
    vehicleMake: "",
    vehicleModel: "",
    vehicleYear: "",
  });

  const handleChange = (inputName) => (e) => {
    e.preventDefault();

    setInputs({
      ...inputs,
      [inputName]: e.target.value,
    });
  };

  const calculatePriceList = useSelector((state) => state.calculatePriceList);
  const {
    loading: costLoading,
    error: costError,
    costCalculation,
  } = calculatePriceList;

  useEffect(() => {
    dispatch(makeCars());
    dispatch(modelCars(inputs.vehicleMake));
    dispatch(yearCars(inputs.vehicleMake, inputs.vehicleModel));
  }, [dispatch, inputs]);

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(vehicleDetailsList([inputs]));
    
    dispatch(calculateCost([["8724","33143"]], [["chevrolet", "camaro", "2013"]], true));
    // props.history.push('/');
  };

  return (
    <div>
      <div className="shipment-box">You are ready to book. CONTINUE!!!</div>
      {instantQuoteDetails.map((e, index) =>
        Array.from({length: e.numberOfCars}, (v, i) => <Form className="vehicle" onSubmit={submitHandler}>
        <Row>
          <Col xs={12} md={6}>
            <label className="title">Vehicle Details</label>

            <Form.Group controlId="vehicleMake">
              <Form.Label className="form-subtitle" htmlFor="vehicleMake">
                Vehicle Make
              </Form.Label>
              {makeLoading ? (
                <LoadingBox></LoadingBox>
              ) : makeError ? (
                <MessageBox variant="danger">{makeError}</MessageBox>
              ) : (
                <Form.Control
                  as="select"
                  id="vehicleMake"
                  defaultValue={inputs.vehicleMake}
                  onChange={handleChange("vehicleMake")}
                  required
                >
                  {carMakes.map((makes, index) => (
                    <option key={index}>
                      {makes}
                    </option>
                  ))}
                </Form.Control>
              )}
            </Form.Group>

            <Form.Group controlId="vehicleModel">
              <Form.Label className="form-subtitle" htmlFor="vehicleModel">
                Vehicle Model
              </Form.Label>
              {modelLoading ? (
                <LoadingBox></LoadingBox>
              ) : modelError ? (
                <MessageBox variant="danger">{modelError}</MessageBox>
              ) : (
                <Form.Control
                  as="select"
                  id="vehicleModel"
                  defaultValue={inputs.vehicleModel}
                  onChange={handleChange("vehicleModel")}
                  required
                >
                  {carModels.map((models, index) => (
                    <option key={index}>
                      {models}
                    </option>
                  ))}
                </Form.Control>
              )}
            </Form.Group>

            <Form.Group controlId="vehicleYear">
              <Form.Label className="form-subtitle" htmlFor="vehicleYear">
                Vehicle Year
              </Form.Label>
              {yearLoading ? (
                <LoadingBox></LoadingBox>
              ) : yearError ? (
                <MessageBox variant="danger">{yearError}</MessageBox>
              ) : (
                <Form.Control
                  as="select"
                  id="vehicleYear"
                  defaultValue={inputs.vehicleYear}
                  onChange={handleChange("vehicleYear")}
                  required
                >
                  {carYears.map((years, index) => (
                    <option key={index}>
                      {years}
                    </option>
                  ))}
                </Form.Control>
              )}
            </Form.Group>
          </Col>
          <Col xs={12} md={6}>
            {/* <Form.Label className="title">Information</Form.Label> */}
            <Form.Group controlId="formGroupMileage">
              <Form.Label className="form-subtitle">Mileage</Form.Label>
              <Form.Control type="text" placeholder="Mileage" required />
            </Form.Group>
            <Form.Group controlId="availableVinNumber">
              <Form.Label className="form-subtitle">Vin Number</Form.Label>
              <Form.Control type="text" placeholder="Vin Number" required />
            </Form.Group>
            <Form.Group controlId="availableVinNumber">
              <Form.Label className="form-subtitle">Vin Number</Form.Label>
              <Form.Control type="text" placeholder="Vin Number" required />
            </Form.Group>
            <Form.Group className="radio">
              <Form.Label className="radio-subtitle">Transport type</Form.Label>
              <div
                className="radio-check"
              >
                <Form.Check
                  type="radio"
                  label="Door-to-door"
                  id="dooor"
                  name="transportType"
                  value="Door-to-door"
                />
                <Form.Check
                  type="radio"
                  label="Open"
                  id="open"
                  name="transportType"
                  value="Open"
                />
                <Form.Check
                  type="radio"
                  label="Enclosed"
                  id="enclosed"
                  name="transportType"
                  value="Enclosed"
                />
                <Form.Check
                  type="radio"
                  label="Expired"
                  id="expired"
                  name="transportType"
                  value="Expired"
                />
                <Form.Check
                  type="radio"
                  label="Hawaii"
                  id="hawai"
                  name="transportType"
                  value="Hawaii"
                />
              </div>
            </Form.Group>
            <Form.Group className="radio">
              <Form.Label className="radio-subtitle">
                Is it operable?{" "}
              </Form.Label>
              <div className="radio-check">
                <Form.Check
                  type="radio"
                  label="Yes"
                  id="op-yes"
                  name="isItOperadable"
                  value="Yes"
                />
                <Form.Check
                  type="radio"
                  label="No"
                  id="op-no"
                  name="isItOperadable"
                  value="No"
                />
              </div>
            </Form.Group>
            <Button type="submit">Send</Button>
          </Col>
        </Row>
      </Form>)

      )}
    </div>
  );
}
