import React, { useState, useEffect } from "react";
import { Form } from "react-bootstrap";
import { BsArrowRightShort } from "react-icons/bs";
import validateQuote from "./validateQuote";
import { useHistory } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { zipCodeListFrom } from "../actions/zipCodeActions";
import { zipCodeListTo } from "../actions/zipCodeActions";
import { instantQuoteList } from "../actions/orderFormActions";

const InstantQuote = () => {

  const dispatch = useDispatch();

  const zipCodeFromList = useSelector((state) => state.zipCodeFromList);
  const zipCodeToList = useSelector((state) => state.zipCodeToList);

  const { loading: zipFromLoading, error: zipFromError, zipCodesFrom } = zipCodeFromList;
  const { loading: zipToLoading, error: zipToError, zipCodesTo } = zipCodeToList;

  const [inputs, setInputs] = useState({
    zipFrom: "",
    zipTo: "",
    pickUpDate: "",
    dropOffDate: "",
    numberOfCars: "",
  });

  const HISTORY = useHistory();



  const Continue = (e) => {
    e.preventDefault();
      dispatch(zipCodeListFrom(inputs.zipFrom));
      dispatch(zipCodeListTo(inputs.zipTo));
      dispatch(instantQuoteList([inputs]));
      console.log("Berna" + inputs.zipTo);
      HISTORY.push({ pathname: "/checkout", state: inputs });
  };

  const handleChange = (inputName) => (e) => {
    e.preventDefault();

    setInputs({
      ...inputs,
      [inputName]: e.target.value,
    });
  };

  return (
    <Form className="instantquote p-3 mb-5" novalidateQuote>
      <Form.Group controlId="formGroupFrom">
        <Form.Label className="form-subtitle">Transport car FROM</Form.Label>
        <Form.Control
          type="text"
          placeholder="ZIP or CITY"
          name="zipFrom"
          onChange={handleChange("zipFrom")}
          defaultValue={inputs.zipFrom}
        />
        <GetError inputName={"zipFrom"}></GetError>
      </Form.Group>
      <Form.Group controlId="formGroupTo">
        <Form.Label className="form-subtitle">Transport car TO</Form.Label>
        <Form.Control
          type="text"
          placeholder="ZIP or CITY"
          name="zipTo"
          onChange={handleChange("zipTo")}
          defaultValue={inputs.zipTo}
        />
        <GetError inputName={"zipTo"}></GetError>
      </Form.Group>
      <Form.Group controlId="pickUpDate">
        <Form.Label className="form-subtitle">Pick Up Date</Form.Label>
        <Form.Control
          type="date"
          onChange={handleChange("pickUpDate")}
          name="pickUpDate"
          defaultValue={inputs.pickUpDate}
        ></Form.Control>
      </Form.Group>
      <Form.Group controlId="dropOffDate">
        <Form.Label className="form-subtitle">Drop Off Date</Form.Label>
        <Form.Control
          type="date"
          onChange={handleChange("dropOffDate")}
          name="dropOffDate"
          defaultValue={inputs.dropOffDate}
        ></Form.Control>
      </Form.Group>
      <Form.Group controlId="selectNumberOfCars">
        {/* <Form.Label>Number Of Cars</Form.Label> */}
        <Form.Control
          as="select"
          className="dropdown"
          onChange={handleChange("numberOfCars")}
          name="numberOfCars"
          defaultValue={inputs.numberOfCars}
        >
          <option>Number Of Cars</option>
          <option>1</option>
          <option>2</option>
          <option>3</option>
          <option>4</option>
          <option>5</option>
          <option>6</option>
          <option>7</option>
          <option>8</option>
          <option>9</option>
          <option>10</option>
        </Form.Control>
      </Form.Group>
      <button className="btn button" onClick={Continue}>
        Vehicle Details
        <BsArrowRightShort />
      </button>
    </Form>
  );
};

export default InstantQuote;
