import {
  NEW_ORDER_INSTANT_QUOTE,
  NEW_ORDER_FORM_VEHICLE_DETAILS,
  NEW_ORDER_INFORMAION_DETAILS,
  } from '../constants/orderFormConstants';
  
  export const orderFormInstantQuoteReducer = (state = { instantQuoteDetails: [] }, action) => {
    switch (action.type) {
      case NEW_ORDER_INSTANT_QUOTE:
        return { ...state, instantQuoteDetails: action.payload };
      default:
        return state;
    }
  };

  export const orderFormVehicleDetailsReducer = (state = { vehicleDetails: [] }, action) => {
    switch (action.type) {
      case NEW_ORDER_FORM_VEHICLE_DETAILS:
        return { ...state, vehicleDetails: action.payload };
      default:
        return state;
    }
  };

  export const orderFormInformationDetailsReducer = (state = { informationDetails: [] }, action) => {
    switch (action.type) {
      case NEW_ORDER_INFORMAION_DETAILS:
        return { ...state, informationDetails: action.payload };
      default:
        return state;
    }
  };

  export const orderFormPaymentMethodReducer = (state = { paymentMethods: [] }, action) => {
    switch (action.type) {
      case CART_SAVE_PAYMENT_METHOD:
        return { ...state, paymentMethods: action.payload };
      default:
        return state;
    }
  };
