import React, { useEffect, useState } from "react";
import { Form } from "react-bootstrap";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import { useDispatch, useSelector } from 'react-redux';
import { makeCars, modelCars } from "../actions/carActions";
import { saveVehicleDetails } from "../actions/orderFormActions";

export default function VehicleDetailsForm(props) {
  const dispatch = useDispatch();
  const { handleChange, checkoutForm } = props;

  const carMakeList = useSelector((state) => state.carMakeList);
  const carModelList = useSelector((state) => state.carModelList);
  
  const {loading: makeLoading, error: makeError, carMakes } = carMakeList;
  const {loading: modelLoading, error: modelError, carModels } = carModelList;

  
  useEffect(() => {
    dispatch(makeCars());
    dispatch(modelCars());
  },[])

  // const [vehicleMake, setVehicleMake] = useState('');
  // const [vehicleModel, setVehicleModel] = useState('');
  // const [vehicleSize, setVehicleSize] = useState('');
  // const submitHandler = (e) => {
  //   e.preventDefault();
  //   dispatch(saveVehicleDetails({vehicleMake}));
  //   TODO: dispatch vehicle details actions
  //   props.HISTORY.push('/');
  // }
    return (
      <div>
      <div className="shipment-box">You are ready to book. CONTINUE!!!</div>
      <Form className="vehicle" onSubmit={submitHandler}>
        <div className="row">
          <div className="col-sm-12 col-md-6 col-lg-6 col-xl-6">
            <label className="title">Vehicle Details</label>
            
            <Form.Group controlId="vehicleMake">
              <Form.Label className="form-subtitle" htmlFor="vehicleMake">Vehicle Make</Form.Label>
              {makeLoading ? (<LoadingBox></LoadingBox>) :
              makeError?(<MessageBox variant="danger">{makeError}</MessageBox>): 
              (
                <Form.Control as="select" defaultValue={checkoutForm.vehicleMake} onChange={handleChange("vehicleMake")} required>
                  {
                    carMakes.map((makes, index) => (
                      <option key={index}>{makes}</option> 

                    ))
                  }
                </Form.Control>)
              }
            </Form.Group>

            <Form.Group controlId="vehicleModel">
              <Form.Label className="form-subtitle" htmlFor="vehicleModel">Vehicle Model</Form.Label>
              {modelLoading ? (<LoadingBox></LoadingBox>) :
              modelError?(<MessageBox variant="danger">{modelError}</MessageBox>): 
              (
                <Form.Control as="select" defaultValue={checkoutForm.vehicleModel} onChange={handleChange("vehicleModel")} required>
                  {
                    carModels.map((makes, index) => (
                      <option key={index}>{makes}</option> 

                    ))
                  }
                </Form.Control>)
              }
            </Form.Group>
            
          </div>
          <div className="col-sm-12 col-md-6 col-lg-6 col-xl-6">
            <label className="title">Confirmation Details</label>
            <Form.Group controlId="formGroupCopy">
              <Form.Label className="form-subtitle">
                Send a copy of the quote to
              </Form.Label>
              <Form.Control type="email" placeholder="Your Email" defaultValue={checkoutForm.email} onChange={handleChange("email")}/>
            </Form.Group>
            <Form.Group controlId="availableDate">
              <Form.Label className="form-subtitle">
                First Available Date
              </Form.Label>
              <Form.Control
                type="date" defaultValue={checkoutForm.availableDate} onChange={handleChange("availableDate")}
              ></Form.Control>
            </Form.Group>
            <Form.Group controlId="formGroupPhone">
              <Form.Label className="form-subtitle">Phone</Form.Label>
              <Form.Control type="tel" placeholder="Phone" defaultValue={checkoutForm.phone} onChange={handleChange("phone")}/>
            </Form.Group>
            <Form.Group className="radio">
              <Form.Label className="radio-subtitle">
                Is it operable?{" "}
              </Form.Label>
              <div className="radio-check" defaultValue={checkoutForm.isItOperadable} onChange={handleChange("isItOperadable")}>
                <Form.Check
                  type="radio"
                  label="Yes"
                  id="op-yes"
                  name="isItOperadable"
                  value="Yes"
                />
                <Form.Check
                  type="radio"
                  label="No"
                  id="op-no"
                  name="isItOperadable"
                  value="No"
                />
              </div>
            </Form.Group>
          </div>
          <div className="col-sm-12 col-md-12 col-lg-12 col-xl-12">
            <Form.Group controlId="formGroupAggree">
              <Form.Label className="aggreement">
                By providing your information, you agree to our privacy policy
                and SMS notifications. Up to 8msgs/mo. Msg&data rates may apply.
                Text STOP to unsubscribe.
              </Form.Label>
            </Form.Group>
          </div>
        </div>
      </Form>
    </div>
    );
  }
