import React, { useEffect, useState } from "react";
import { Form } from "react-bootstrap";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import { useDispatch, useSelector } from 'react-redux';
import { modelCars } from "../actions/carActions";
import axios from "axios";

export default function VehicleDetailsForm(props) {
  const { handleChange, checkoutForm } = props;
  
  const [carModelList, setCarModelList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);
  

  useEffect(() => {
    const fecthData = async () => {
      try {
        setLoading(true);
        const { data } = await axios({
          method: 'GET',
          url: 'http://165.227.108.163:5050/cars/get_all_makes/cars/get_all_makes/',
          headers: {
            'cache-Control': 'no-cache',
            'content-Type': 'application/json',
            'Access-Control-Allow-Origin': '*',
            'Access-Control-Allow-Methods': 'GET, POST, OPTIONS, HEAD',
            'Access-Control-Allow-Headers': 'Authorization, Origin, X-Requested-With, Content-Type, Accept'
        },
      })
        setLoading(false);
        setCarModelList(data);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };
    fecthData();
  }, []);
    return (
      <div>
      <div className="shipment-box">You are ready to book. CONTINUE!!!</div>
      <Form className="vehicle">
        <div className="row">
          <div className="col-sm-12 col-md-6 col-lg-6 col-xl-6">
            <label className="title">Vehicle Details</label>
            <Form.Group controlId="vehicleYear">
              <Form.Label className="form-subtitle">Vehicle Year</Form.Label>
              {loading ? (<LoadingBox></LoadingBox>) :
              error?(<MessageBox variant="danger">{error}</MessageBox>): 
              (
                <Form.Control as="select" defaultValue={checkoutForm.vehicleYear} onChange={handleChange("vehicleYear")}>
                  {
                    carModelList.map((carModel, index) => (
                      <option key={index}>{carModel.makes}</option>
                    ))
                  }
                </Form.Control>)
              }
            </Form.Group>
            <Form.Group controlId="vehicleMake">
              <Form.Label className="form-subtitle">Vehicle Make</Form.Label>
              <Form.Control as="select" defaultValue={checkoutForm.vehicleMake} onChange={handleChange("vehicleMake")}>
                <option>Vehicle Make</option>
                <option>AC</option>
                <option>Alfa Romeo</option>
                <option>Juliet</option>
              </Form.Control>
            </Form.Group>
            <Form.Group controlId="vehicleModel">
              <Form.Label className="form-subtitle">Vehicle Model</Form.Label>
              <Form.Control as="select" defaultValue={checkoutForm.vehicleModel} onChange={handleChange("vehicleModel")}>
                <option>A odel</option>
                <option>B model</option>
                <option>C model</option>
              </Form.Control>
            </Form.Group>
          </div>
          <div className="col-sm-12 col-md-6 col-lg-6 col-xl-6">
            <label className="title">Confirmation Details</label>
            <Form.Group controlId="formGroupCopy">
              <Form.Label className="form-subtitle">
                Send a copy of the quote to
              </Form.Label>
              <Form.Control type="email" placeholder="Your Email" defaultValue={checkoutForm.email} onChange={handleChange("email")}/>
            </Form.Group>
            <Form.Group controlId="availableDate">
              <Form.Label className="form-subtitle">
                First Available Date
              </Form.Label>
              <Form.Control
                type="date" defaultValue={checkoutForm.availableDate} onChange={handleChange("availableDate")}
              ></Form.Control>
            </Form.Group>
            <Form.Group controlId="formGroupPhone">
              <Form.Label className="form-subtitle">Phone</Form.Label>
              <Form.Control type="tel" placeholder="Phone" defaultValue={checkoutForm.phone} onChange={handleChange("phone")}/>
            </Form.Group>
            <Form.Group className="radio">
              <Form.Label className="radio-subtitle">
                Is it operable?{" "}
              </Form.Label>
              <div className="radio-check" defaultValue={checkoutForm.isItOperadable} onChange={handleChange("isItOperadable")}>
                <Form.Check
                  type="radio"
                  label="Yes"
                  id="op-yes"
                  name="isItOperadable"
                  value="Yes"
                />
                <Form.Check
                  type="radio"
                  label="No"
                  id="op-no"
                  name="isItOperadable"
                  value="No"
                />
              </div>
            </Form.Group>
          </div>
          <div className="col-sm-12 col-md-12 col-lg-12 col-xl-12">
            <Form.Group controlId="formGroupAggree">
              <Form.Label className="aggreement">
                By providing your information, you agree to our privacy policy
                and SMS notifications. Up to 8msgs/mo. Msg&data rates may apply.
                Text STOP to unsubscribe.
              </Form.Label>
            </Form.Group>
          </div>
        </div>
      </Form>
    </div>
    );
  }
