import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Container, Form } from "react-bootstrap";
import { Link } from "react-router-dom";
import { FaFacebookF, FaYoutube, FaInstagram } from "react-icons/fa";
import { SiGmail } from "react-icons/si";
import validate from "./validate";
import useForm from "./useForm";
import { signin } from '../actions/userActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';

export default function Signin(props) {
  const [values, setValues] = useState({
    email: "",
    password: "",
  });

  const redirect = props.location.search
    ? props.location.search.split('=')[1]
    : '/';

  const userSignin = useSelector((state) => state.userSignin);
  const { userInfo, loading, error } = userSignin;

  const dispatch = useDispatch();
  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(signin([values]));
  };
  useEffect(() => {
    if (userInfo) {
      props.history.push(redirect);
    }
  }, [props.history, redirect, userInfo]);
  return (
    <Container className="single-forms">
      <h3 className="title">Sign In</h3>
      <Form className="box" onSubmit={submitHandler}>
        <Form.Group controlId="signinEmail" className="email">
          <Form.Label className="form-subtitle">E-mail</Form.Label>
          <Form.Control
            type="email"
            id="email"
            placeholder="Enter"
            required
            onChange={(e) => setValues(e.target.email)}
          />
          {/* {errors.email && <p>{errors.email}</p>} */}
        </Form.Group>
        <div className="links">
          <Link to="/forgetpassword">Forget Password?</Link>
        </div>
        <Form.Group controlId="signinPassword">
          <Form.Label className="form-subtitle">Password</Form.Label>
          <Form.Control
            type="password"
            id="password"
            placeholder="Enter"
            required
            onChange={(e) => setValues(e.target.password)}
          />
          {/* {errors.password && <p>{errors.password}</p>} */}
        </Form.Group>

        <button type="submit" color="primary" className="btn button">
          Sign In 
        </button>
        <div className="social">
          <Link to="/" className="facebook">
            <FaFacebookF />
          </Link>
          <Link to="/" className="youtube">
            <FaYoutube />
          </Link>
          <Link onTouchCancelCapture="/" className="instagram">
            <FaInstagram />
          </Link>
          <Link to="/" className="gmail">
            <SiGmail />
          </Link>
        </div>
      </Form>
      <div className="alt-box">
        <div className="links">
          Don't have an account?
          <Link to="/register" className="link-title">
            {" "}
            Register
          </Link>
        </div>
      </div>
      <div className="alt-links">
        <Link to="/">Terms</Link>
        <Link to="/">Privacy</Link>
        <Link to="/">Security</Link>
        <Link to="/">Contact</Link>
      </div>
    </Container>
  );
};
