import React, { useEffect, useState } from "react";
import { Form } from "react-bootstrap";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import { useDispatch, useSelector } from 'react-redux';
import { makeCars, modelCars, yearCars } from "../actions/carActions";
import { saveVehicleDetails } from "../actions/orderFormActions";

export default function VehicleDetailsForm(props) {
  const dispatch = useDispatch();
  // const { handleChange, checkoutForm } = props;

  const carMakeList = useSelector((state) => state.carMakeList);
  const carModelList = useSelector((state) => state.carModelList);
  const carYearList = useSelector((state) => state.carYearList);
  
  const {loading: makeLoading, error: makeError, carMakes } = carMakeList;
  const {loading: modelLoading, error: modelError, carModels } = carModelList;
  const {loading: yearLoading, error: yearError, carYears } = carYearList;
  
  const [inputs, setInputs] = useState({
    vehicleMake: "",
    vehicleModel: "",
    vehicleYear: ""
  })

  const handleChange = (inputName) => (e) => {
    e.preventDefault();

    setInputs({
      ...inputs,
      [inputName]: e.target.value,
    });
  };

  
  useEffect(() => {
    dispatch(makeCars());
    dispatch(modelCars(inputs.vehicleMake));
     dispatch(yearCars(inputs.vehicleMake, inputs.vehicleModel));
  }, [inputs])
  
  // const submitHandler = (e) => {
  //   e.preventDefault();
  //   dispatch(saveVehicleDetails({vehicleMake}));
  //   // TODO: dispatch vehicle details actions
  //   props.HISTORY.push('/');
  // }
    return (
      <div>
      <div className="shipment-box">You are ready to book. CONTINUE!!!</div>
      <Form className="vehicle">
        <div className="row">
          <div className="col-sm-12 col-md-6 col-lg-6 col-xl-6">
            <label className="title">Vehicle Details</label>
            
            <Form.Group controlId="vehicleMake">
              <Form.Label className="form-subtitle" htmlFor="vehicleMake">Vehicle Make</Form.Label>
              {makeLoading ? (<LoadingBox></LoadingBox>) :
              makeError?(<MessageBox variant="danger">{makeError}</MessageBox>): 
              (
                <Form.Control as="select" defaultValue={inputs.vehicleMake} onChange={handleChange("vehicleMake")} required>
                  {
                    carMakes.map((makes, index) => (
                      <option key={index} value={makes}>{makes}</option> 
                    ))
                  }
                </Form.Control>)
              }
            </Form.Group>

            <Form.Group controlId="vehicleModel">
              <Form.Label className="form-subtitle" htmlFor="vehicleModel">Vehicle Model</Form.Label>
              {modelLoading ? (<LoadingBox></LoadingBox>) :
              modelError?(<MessageBox variant="danger">{modelError}</MessageBox>): 
              (
                <Form.Control as="select"  onChange={handleChange("vehicleModel")} required>
                  {
                    carModels.map((models, index) => (
                      <option key={index} value={models}>{models}</option> 
                    ))
                  }
                </Form.Control>)
              }
            </Form.Group>

            <Form.Group controlId="vehicleYear">
              <Form.Label className="form-subtitle" htmlFor="vehicleYear">Vehicle Year</Form.Label>
              {yearLoading ? (<LoadingBox></LoadingBox>) :
              yearError?(<MessageBox variant="danger">{yearError}</MessageBox>): 
              (
                <Form.Control as="select" onChange={handleChange("vehicleYear")} required>
                  {
                    carYears.map((years, index) => (
                      <option key={index} value={years}>{years}</option> 

                    ))
                  }
                </Form.Control>)
              }
            </Form.Group>
            
          </div>
          <div className="col-sm-12 col-md-6 col-lg-6 col-xl-6">
            {/* <Form.Label className="title">Information</Form.Label> */}
            <Form.Group controlId="formGroupMileage">
              <Form.Label className="form-subtitle">
                Mileage
              </Form.Label>
              <Form.Control type="text" placeholder="Mileage" required/>
            </Form.Group>
            <Form.Group controlId="availableVinNumber">
              <Form.Label className="form-subtitle">
                Vin Number
              </Form.Label>
              <Form.Control type="text" placeholder="Vin Number" required/>
            </Form.Group>
            <Form.Group controlId="availableVinNumber">
              <Form.Label className="form-subtitle">
                Vin Number
              </Form.Label>
              <Form.Control type="text" placeholder="Vin Number" required/>
            </Form.Group>
            <Form.Group className="radio">
              <Form.Label className="radio-subtitle">
                Is it operable?{" "}
              </Form.Label>
              <div className="radio-check">
                <Form.Check
                  type="radio"
                  label="Yes"
                  id="op-yes"
                  name="isItOperadable"
                  value="Yes"
                />
                <Form.Check
                  type="radio"
                  label="No"
                  id="op-no"
                  name="isItOperadable"
                  value="No"
                />
              </div>
            </Form.Group>
          </div> 
        </div>
      </Form>
    </div>
    );
  }
