import Axios from "axios";
import {
  NEW_ORDER_INSTANT_QUOTE,
  NEW_ORDER_FORM_VEHICLE_DETAILS,
  NEW_ORDER_INFORMAION_DETAILS,
  NEW_ORDER_PAYMENT_METHOD,
  CALCULATE_COST_REQUEST,
  CALCULATE_COST_SUCCESS,
  CALCULATE_COST_FAIL,
} from "../constants/orderFormConstants";

export const instantQuoteList = (data) => (dispatch, getState) => {
  dispatch({ type: NEW_ORDER_INSTANT_QUOTE, payload: data });
  localStorage.setItem('instantQuoteDetails', JSON.stringify(data));
};

export const vehicleDetailsList = (data) => (dispatch) => {
  dispatch({ type: NEW_ORDER_FORM_VEHICLE_DETAILS, payload: data });
  localStorage.setItem('vehicleDetails', JSON.stringify(data));
};

export const informationDetailsList = (data) => (dispatch) => {
  dispatch({ type: NEW_ORDER_INFORMAION_DETAILS, payload: data });
  // localStorage.setItem('shippingAddress', JSON.stringify(data));
};

export const savePaymentMethod = (data) => (dispatch) => {
  dispatch({ type: NEW_ORDER_PAYMENT_METHOD, payload: data });
};

export const calculateCost = (zipList, carList, individual) => async (dispatch) => {
  dispatch({ type: CALCULATE_COST_REQUEST, payload: { zipList, carList, individual } });
  try {
    const { data } = await Axios({
      method: "POST",
      url: `http://159.65.45.58/price/`,
      headers: {
        "cache-Control": "no-cache",
        "content-Type": "application/json",
        "Access-Control-Allow-Origin": "*",
      },
      data: {
        "ziplist":zipList,
        "carlist":carList,
        "individual":individual,
      }
    });
    dispatch({ type: CALCULATE_COST_SUCCESS, payload: data });
  } catch (error) {
    dispatch({ type: CALCULATE_COST_FAIL, payload: error.message });
  }
};
