  const {loading: modelLoading, error: modelError, carModels } = carModelList;
  const {loading: yearLoading, error: yearError, carYears } = carYearList;
  
  const [inputs, setInputs] = useState({
    vehicleMake: "",
    vehicleModel: "",
    vehicleYear: ""
  })

  const handleChange = (inputName) => (e) => {
    e.preventDefault();

    setInputs({
      ...inputs,
      [inputName]: e.target.value,
    });
  };

  
  useEffect(() => {
    dispatch(makeCars());
    dispatch(modelCars(inputs));
    console.log("ozgur"+inputs);
    // dispatch(yearCars(vehicleMake, vehicleModel));
