              <Form.Label className="form-subtitle" htmlFor="vehicleMake">Vehicle Make</Form.Label>
              {makeLoading ? (<LoadingBox></LoadingBox>) :
              makeError?(<MessageBox variant="danger">{makeError}</MessageBox>): 
              (
                <Form.Control as="select" defaultValue={inputs.vehicleMake} onChange={handleChange("vehicleMake")} required>
                  {
                    carMakes.map((makes, index) => (
                      <option key={index}>{makes}</option> 
                    ))
                  }
                </Form.Control>)
              }
            </Form.Group>

            <Form.Group controlId="vehicleModel">
              <Form.Label className="form-subtitle" htmlFor="vehicleModel">Vehicle Model</Form.Label>
              {modelLoading ? (<LoadingBox></LoadingBox>) :
              modelError?(<MessageBox variant="danger">{modelError}</MessageBox>): 
              (
                <Form.Control as="select"  onChange={handleChange("vehicleModel")} required>
                  {
                    carModels.map((models, index) => (
                      <option key={index}>{models}</option> 
                    ))
                  }
                </Form.Control>)
              }
            </Form.Group>

            <Form.Group controlId="vehicleYear">
              <Form.Label className="form-subtitle" htmlFor="vehicleYear">Vehicle Year</Form.Label>
              {yearLoading ? (<LoadingBox></LoadingBox>) :
              yearError?(<MessageBox variant="danger">{yearError}</MessageBox>): 
              (
                <Form.Control as="select" onChange={handleChange("vehicleYear")} required>
                  {
                    carYears.map((years, index) => (
                      <option key={index}>{years}</option> 

                    ))
                  }
                </Form.Control>)
              }
            </Form.Group>
            
          </div>
