import Axios from "axios";
import {
  CAR_MODEL_LIST_REQUEST,
  CAR_MODEL_LIST_SUCCESS,
  CAR_MODEL_LIST_FAIL,
  CAR_YEAR_REQUEST,
  CAR_YEAR_SUCCESS,
  CAR_YEAR_FAIL,
  CAR_MAKE_LIST_REQUEST,
  CAR_MAKE_LIST_SUCCESS,
  CAR_MAKE_LIST_FAIL,
} from "../constants/carConstants";

export const makeCars = () => async (dispatch) => {
  dispatch({
    type: CAR_MAKE_LIST_REQUEST,
  });
  try {
    const { data } = await Axios({
      method: "GET",
      url: "http://159.65.45.58:5000/cars/get_all_makes/",
      headers: {
        "cache-Control": "no-cache",
        "content-Type": "application/json",
        "Access-Control-Allow-Origin": "*",
      },
    });
    dispatch({ type: CAR_MAKE_LIST_SUCCESS, payload: data });
  } catch (error) {
    dispatch({ type: CAR_MAKE_LIST_FAIL, payload: error.message });
  }
};

export const modelCars = () => async (dispatch) => {
  dispatch({
    type: CAR_MODEL_LIST_REQUEST,
  });
  try {
    const { data } = await Axios({
      method: "POST",
      url: "http://159.65.45.58:5000/cars/get_car_data_with_model",
      data: {
        make: 'dodge',
      },
      headers: {
        "cache-Control": "no-cache",
        "content-Type": "application/json",
        "Access-Control-Allow-Origin": "*",
      },
    });
    dispatch({ type: CAR_MODEL_LIST_SUCCESS, payload: data });
  } catch (error) {
    dispatch({ type: CAR_MODEL_LIST_FAIL, payload: error.message });
  }
};

export const yearCars = () => async (dispatch) => {
  dispatch({
    type: CAR_YEAR_REQUEST,
  });
  try {
    const { data } = await Axios({
      method: "GET",
      url: "http://159.65.45.58:5000/cars/get_car_size_with_make_and_model_year",
      headers: {
        "cache-Control": "no-cache",
        "content-Type": "application/json",
        "Access-Control-Allow-Origin": "*",
      },
    });
    dispatch({ type: CAR_YEAR_SUCCESS, payload: data });
  } catch (error) {
    dispatch({ type: CAR_YEAR_FAIL, payload: error.message });
  }
};
