import React, { useEffect } from "react";
import { Form } from "react-bootstrap";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import { useDispatch, useSelector } from "react-redux";
import { modelCars, yearCars } from "../actions/carActions";

export default function VehicleDetailsForm(props) {
  const dispatch = useDispatch();
  const { handleChange, checkoutForm } = props;

  const carModelList = useSelector((state) => state.carModelList);
  const carYearList = useSelector((state) => state.carYearList);

  const { loadingModel, errorModel, carModels } = carModelList;
  
  const { loadingYear, errorYear, carYears } = carYearList;

  useEffect(() => {
    dispatch(modelCars());
    dispatch(yearCars());
  }, []);
  return (
    <div>
      <div className="shipment-box">You are ready to book. CONTINUE!!!</div>
      <Form className="vehicle">
        <div className="row">
          <div className="col-sm-12 col-md-6 col-lg-6 col-xl-6">
            <label className="title">Vehicle Details</label>

            <Form.Group controlId="vehicleMake">
              <Form.Label className="form-subtitle">Vehicle Make</Form.Label>
              {/* {loadingModel ? (
                <LoadingBox></LoadingBox>
              ) : errorModel ? (
                <MessageBox variant="danger">{errorModel}</MessageBox>
              ) : (
                <Form.Control
                  as="select"
                  defaultValue={checkoutForm.vehicleYear}
                  onChange={handleChange("vehicleMake")}
                >
                  {carModels.map((carModel, {index}) => (
                    <option key={index}>{carModel.title}</option>
                  ))}
                </Form.Control>
              )} */}
            </Form.Group>
            <Form.Group controlId="vehicleModel">
              <Form.Label className="form-subtitle">Vehicle Model</Form.Label>
              {/* {loadingModel ? (
                <LoadingBox></LoadingBox>
              ) : errorModel ? (
                <MessageBox variant="danger">{errorModel}</MessageBox>
              ) : (
                <Form.Control
                  as="select"
                  defaultValue={checkoutForm.vehicleYear}
                  onChange={handleChange("vehicleModel")}
                >
                  {carModels.map((carModel, {index}) => (
                    <option key={index}>{carModel.title}</option>
                  ))}
                </Form.Control>
              )} */}
            </Form.Group>
            <Form.Group controlId="vehicleYear">
              <Form.Label className="form-subtitle">Vehicle Year</Form.Label>
              {loadingYear ? (
                <LoadingBox></LoadingBox>
              ) : errorYear ? (
                <MessageBox variant="danger">{errorYear}</MessageBox>
              ) : (
                <Form.Control
                  as="select"
                  defaultValue={checkoutForm.vehicleYear}
                  onChange={handleChange("vehicleYear")}
                >
                  {carYears.map((carYear, {index}) => (
                    <option key={index}>{carYear.title}</option>
                  ))}
                </Form.Control>
              )}
            </Form.Group>
          </div>
          <div className="col-sm-12 col-md-6 col-lg-6 col-xl-6">
            <label className="title">Confirmation Details</label>
            <Form.Group controlId="formGroupCopy">
              <Form.Label className="form-subtitle">
                Send a copy of the quote to
              </Form.Label>
              <Form.Control
                type="email"
                placeholder="Your Email"
                defaultValue={checkoutForm.email}
                onChange={handleChange("email")}
              />
            </Form.Group>
            <Form.Group controlId="availableDate">
              <Form.Label className="form-subtitle">
                First Available Date
              </Form.Label>
              <Form.Control
                type="date"
                defaultValue={checkoutForm.availableDate}
                onChange={handleChange("availableDate")}
              ></Form.Control>
            </Form.Group>
            <Form.Group controlId="formGroupPhone">
              <Form.Label className="form-subtitle">Phone</Form.Label>
              <Form.Control
                type="tel"
                placeholder="Phone"
                defaultValue={checkoutForm.phone}
                onChange={handleChange("phone")}
              />
            </Form.Group>
            <Form.Group className="radio">
              <Form.Label className="radio-subtitle">
                Is it operable?{" "}
              </Form.Label>
              <div
                className="radio-check"
                defaultValue={checkoutForm.isItOperadable}
                onChange={handleChange("isItOperadable")}
              >
                <Form.Check
                  type="radio"
                  label="Yes"
                  id="op-yes"
                  name="isItOperadable"
                  value="Yes"
                />
                <Form.Check
                  type="radio"
                  label="No"
                  id="op-no"
                  name="isItOperadable"
                  value="No"
                />
              </div>
            </Form.Group>
          </div>
          <div className="col-sm-12 col-md-12 col-lg-12 col-xl-12">
            <Form.Group controlId="formGroupAggree">
              <Form.Label className="aggreement">
                By providing your information, you agree to our privacy policy
                and SMS notifications. Up to 8msgs/mo. Msg&data rates may apply.
                Text STOP to unsubscribe.
              </Form.Label>
            </Form.Group>
          </div>
        </div>
      </Form>
    </div>
  );
}
