import * as yup from "yup";

export const zipFrom = yup.object().shape({
  zipFrom: yup
    .string()
    .min(3, "Too Short!")
    .max(5, "Too Long!")
    .required("Required"),
});

export const zipTo = yup.object().shape({
  zipTo: yup
    .string()
    .min(3, "Too Short!")
    .max(5, "Too Long!")
    .required("Required"),
});

export const pickUpDate = yup.object().shape({
  pickUpDate: yup.date().nullable(),
});

export const dropOffDate = yup.object().shape({
  dropOffDate: yup.date().nullable(),
});

export const numberOfCars = yup.object().shape({
  numberOfCars: yup.string().ensure().required("Required!"),
});
