import {
    ORDER_SAVE_VEHICLE_DETAILS,
  } from '../constants/orderFormConstants';
  
  export const orderFormVehicleDetailsReducer = (state = { vehicleDetails: [] }, action) => {
    switch (action.type) {
      case ORDER_SAVE_VEHICLE_DETAILS:
        return { ...state, vehicleDetails: action.payload };
      default:
        return state;
    }
  };
