import React, { Component } from "react";
import { Link } from "react-router-dom";
import InstantQuote from "../forms/InstantQuote";
import individual from "../assets/individual-b.png";
import brief from "../assets/briefcase-b.png";
import truck from "../assets/shipped-b.png";
import estate from "../assets/real-estate-b.png";
import { GoX } from "react-icons/go";

export default class Sidebar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      active: "",
    };
  }
  componentDidMount() {
    window.addEventListener("scroll", this.handleScroll);
  }

  componentWillUnmount() {
    window.removeEventListener("scroll", this.handleScroll);
  }

  handleScroll = () => {
    if (window.scrollY > 0) {
      document.querySelector(".toolbar").className = "toolbar scroll";
    } else {
      document.querySelector(".toolbar").className = "toolbar";
    }
  };
  render() {
    return (
      <aside>
        <div className="toolbar">
          <ul>
            <li
              className={`${
                this.state.active === "linkCOntent1" ? "link-selected" : ""
              }`}
              onClick={() => this.setState({ active: "linkCOntent1" })}
            >
              <Link>
                <div className="title">Content 1</div>
              </Link>
            </li>
            <li
              className={`${
                this.state.active === "linkCOntent2" ? "link-selected" : ""
              }`}
              onClick={() => this.setState({ active: "linkCOntent2" })}
            >
              <Link>
                <div className="title">Content 2</div>
              </Link>
            </li>

          </ul>

          <div
            className={`toolbar-tools ${
              this.state.active === "linkCOntent1" ? "open" : ""
            }`}
            onClick={() => this.setState({ active: "linkCOntent1" })}
          >
            <Link to="/" class="toolbar-close" id="cancel">
              <span>
                <GoX />
              </span>
            </Link>
            <InstantQuote />
          </div>
        </div>
      </aside>
    );
  }
}
