import React, { useState, useEffect, useMemo } from "react";
import { Form } from "react-bootstrap";
import { BsArrowRightShort } from "react-icons/bs";
import { useHistory } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { zipCodeListFrom } from "../actions/zipCodeActions";
import { zipCodeListTo } from "../actions/zipCodeActions";
import { newOrder } from "../actions/orderFormActions";

const endPoint = "https://jsonplaceholder.typicode.com/users";

const InstantQuote = () => {

  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchText, setSearchText] = useState("");

  useEffect(() => {
    (async function () {
      const res = await fetch(endPoint);
      const data = await res.json();
      setUsers(data);
      setLoading(false);
    })();
  }, []);

  
  const HISTORY = useHistory();
  const dispatch = useDispatch();

  const newOrderList = useSelector((state) => state.newOrderList);
  const { orderNew } = newOrderList;

  const [inputs, setInputs] = useState({
    zipFrom: "",
    zipTo: "",
    pickUpDate: "",
    dropOffDate: "",
    numberOfCars: "",
  });

  const handleChange = (inputName) => (e) => {
    e.preventDefault();

    setInputs({
      ...inputs,
      [inputName]: e.target.value,
    });
  };

  const Continue = (e) => {
    e.preventDefault();
      dispatch(zipCodeListFrom(inputs.zipFrom));
      dispatch(zipCodeListTo(inputs.zipTo));
      dispatch(newOrder("33143","08724","10/03/2021","20/04/2021","3"));
      // console.log("Berna" + inputs.zipTo);
      HISTORY.push({ pathname: "/checkout", state: inputs });
  };

  const filterData = useMemo(() => {
    if (searchText.length > 3) {
      return users.filter((user) => user.address.zipcode.includes(searchText));
    }
    return null;
  }, [users, searchText]);

  if (loading) return <p>Loading...</p>;

  return (
    <div className="App">
      <h1>Type in searchbox to get the user with matching zipcode</h1>
      <input
        type="text"
        placeholder="Search Zip"
        value={searchText}
        onChange={(e) => setSearchText(e.target.value)}
      />
      <ul>
        {filterData &&
          filterData.map((data) => (
            <li key={data.id}>{data.address.zipcode}</li>
          ))}
      </ul>
    </div>
  );

  return (
    <Form className="instantquote p-3 mb-5" novalidateQuote>
      <Form.Group controlId="formGroupFrom">
        <Form.Label className="form-subtitle">Transport car FROM</Form.Label>
        <Form.Control
          type="text"
          placeholder="ZIP or CITY"
          name="zipFrom"
          onChange={handleChange("zipFrom")}
          defaultValue={inputs.zipFrom}
        />
      </Form.Group>
      <Form.Group controlId="formGroupTo">
        <Form.Label className="form-subtitle">Transport car TO</Form.Label>
        <Form.Control
          type="text"
          placeholder="ZIP or CITY"
          name="zipTo"
          onChange={handleChange("zipTo")}
          defaultValue={inputs.zipTo}
        />
      </Form.Group>
      <Form.Group controlId="pickUpDate">
        <Form.Label className="form-subtitle">Pick Up Date</Form.Label>
        <Form.Control
          type="date"
          onChange={handleChange("pickUpDate")}
          name="pickUpDate"
          defaultValue={inputs.pickUpDate}
        ></Form.Control>
      </Form.Group>
      <Form.Group controlId="dropOffDate">
        <Form.Label className="form-subtitle">Drop Off Date</Form.Label>
        <Form.Control
          type="date"
          onChange={handleChange("dropOffDate")}
          name="dropOffDate"
          defaultValue={inputs.dropOffDate}
        ></Form.Control>
      </Form.Group>
      <Form.Group controlId="selectNumberOfCars">
        {/* <Form.Label>Number Of Cars</Form.Label> */}
        <Form.Control
          as="select"
          className="dropdown"
          onChange={handleChange("numberOfCars")}
          name="numberOfCars"
          defaultValue={inputs.numberOfCars}
        >
          <option>Number Of Cars</option>
          {
            [...Array(10).keys()].map( x => (
              <option key={x+1} value={x+1}>{x+1}</option>
            ))
          }
        </Form.Control>
      </Form.Group>
      <button className="btn button" onClick={Continue}>
        Vehicle Details
        <BsArrowRightShort />
      </button>
    </Form>
  );
};

export default InstantQuote;
