import React from "react";
import { Form } from "react-bootstrap";
import Cards from "react-credit-cards";
import "react-credit-cards/es/styles-compiled.css";
import { useDispatch } from "react-redux";

export default function BookShipmentForm() {
  state = {
    cvc: " ",
    expiry: "",
    focus: "",
    name: "",
    number: "",
  };

  handleInputFocus = (e) => {
    this.setState({ focus: e.target.name });
  };

  handleInputChange = (e) => {
    const { name, value } = e.target;

    this.setState({ [name]: value });
  };


  const dispatch = useDispatch();
  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(savePaymentMethod(paymentMethod));
    // props.history.push('/');
  };

    return (
      <div id="PaymentForm">
        <div className="row">
          <div className="col-sm-12 col-md-8 col-lg-8 col-xl-8">
            <div className="payment-box">
              <div className="title">$0 Due now</div>
              Your credit card will not be charged until the order is assigned
              to a carrier.
            </div>
          </div>
        </div>
        <div className="row">
          <div className="col-sm-12 col-md-8 col-lg-8 col-xl-8">
            <Form className="payment shadow p-3 mb-5" onSubmit={submitHandler}>
              <div className="row">
                <div className="col-sm-12 col-md-12 col-lg-12 col-xl-12">
                  <Form.Label className="form-subtitle">Card number</Form.Label>
                  <Form.Control
                    type="tel"
                    name="number"
                    placeholder="Card Number"
                    onChange={this.handleInputChange}
                    onFocus={this.handleInputFocus}
                  />
                </div>
              </div>
              <div className="row">
                <div className="col-sm-12 col-md-12 col-lg-12 col-xl-12">
                  <Form.Label className="form-subtitle">
                    Full name on card
                  </Form.Label>
                  <Form.Control
                    type="text"
                    name="name"
                    placeholder="Name"
                    onChange={this.handleInputChange}
                    onFocus={this.handleInputFocus}
                  />
                </div>
              </div>
              <div className="row">
                <div className="col-sm-12 col-md-6 col-lg-6 col-xl-6">
                  <Form.Label className="form-subtitle">
                    Expiration date
                  </Form.Label>
                  <Form.Control
                    type="tel"
                    name="expiry"
                    placeholder="MM / YY"
                    onChange={this.handleInputChange}
                    onFocus={this.handleInputFocus}
                  />
                </div>
                <div className="col-sm-12 col-md-6 col-lg-6 col-xl-6">
                  <Form.Label className="form-subtitle">
                    Security code
                  </Form.Label>
                  <Form.Control
                    type="tel"
                    name="cvc"
                    placeholder="CVC"
                    onChange={this.handleInputChange}
                    onFocus={this.handleInputFocus}
                  />
                </div>
              </div>
            </Form>
          </div>
          <div className="col-sm-12 col-md-4 col-lg-4 col-xl-4">
            <Cards
              cvc={this.state.cvc}
              expiry={this.state.expiry}
              focused={this.state.focus}
              name={this.state.name}
              number={this.state.number}
            />
          </div>
        </div>
      </div>
    );
  }
