import React from "react";
import { Form } from "react-bootstrap";
import Cards from "react-credit-cards";
import "react-credit-cards/es/styles-compiled.css";

export default class BookShipmentForm extends React.Component {
  state = {
    cvc: " ",
    expiry: "",
    focus: "",
    name: "",
    number: "",
  };

  handleInputFocus = (e) => {
    this.setState({ focus: e.target.name });
  };

  handleInputChange = (e) => {
    const { name, value } = e.target;

    this.setState({ [name]: value });
  };

  render() {
    return (
      <div id="PaymentForm">
        <div className="row">
          <div className="col-sm-12 col-md-8 col-lg-8 col-xl-8">
            <div className="payment-box">
              <div className="title">$0 Due now</div>
              Your credit card will not be charged until the order is assigned
              to a carrier.
            </div>
          </div>
        </div>
        <div className="row">
          <div className="col-sm-12 col-md-8 col-lg-8 col-xl-8">
            <Form className="payment shadow p-3 mb-5" onSubmit={submitHandler}>
              <div>
                <input
                  type="radio"
                  id="paypal"
                  value="PayPal"
                  name="paymentMethod"
                  required
                  checked
                  onChange={(e) => setPaymentMethod(e.target.value)}
                ></input>
                <label htmlFor="paypal">PayPal</label>
              </div>
              <div>
                <input
                  type="radio"
                  id="creditcard"
                  value="CreditCard"
                  name="paymentMethod"
                  required
                  onChange={(e) => setPaymentMethod(e.target.value)}
                ></input>
                <label htmlFor="creditcard">Stripe</label>
              </div>
            </Form>
          </div>
        </div>
      </div>
    );
  }
}
