import { createStore, compose, applyMiddleware, combineReducers } from 'redux';
import thunk from 'redux-thunk';
import { carMakeListReducer, carModelListReducer } from './reducers/carReducers';
import { zipCodeFromReducer, zipCodeToReducer } from './reducers/zipCodeReducers';

const initialState = {};
const reducer = combineReducers({
    carMakeList: carMakeListReducer,
    carModelList: carModelListReducer,
    zipCodeFromList: zipCodeFromReducer,
    zipCodeToList: zipCodeToReducer,
})

const composeEnhancer = window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ || compose;

const store = createStore(reducer, initialState, composeEnhancer(applyMiddleware(thunk)))

export default store;
