import React, { useState, useEffect } from "react";
import { Form } from "react-bootstrap";
import { BsArrowRightShort } from "react-icons/bs";
import validateQuote from "./validateQuote";
import { useHistory } from "react-router-dom";
import { useDispatch, useSelector } from 'react-redux';
import { zipCodeListFrom } from "../actions/zipCodeActions";
import { zipCodeListTo } from "../actions/zipCodeActions";

const InstantQuote = () => {
  const [inputs, setInputs] = useState({
    zipFrom: "",
    zipTo: "",
  })
  const [errors, setErrors] = useState([

  ])
  const HISTORY = useHistory()
  const inputNames = [
    'zipFrom',
    'zipTo'
  ] 

  const dispatch = useDispatch();

  const zipCodeFromList = useSelector((state) => state.zipCodeFromList);
  const zipCodeToList = useSelector((state) => state.zipCodeToList);
  
  // const {loading, error, zipCodesFrom } = zipCodeFromList;
  // const {loadingTo, errorTo, zipCodesTo } = zipCodeToList;

  const doValidate = (inputsInState) => {
    let success = true;
    console.log('RAN', inputsInState);
    inputNames.forEach(inputName => {
      const validation = validateQuote(inputName, inputsInState[inputName]);
      console.log(inputName, validation);
      if (!validation.isValid) { 
        success=false;
        setError(inputName, validation.message);
      }
      else removeError(inputName);
    })
    
    dispatch(zipCodeListFrom(inputsInState.zipFrom));
    dispatch(zipCodeListTo(inputsInState.zipTo));
    console.log("Berna"+ inputsInState.zipTo);
    return success;
  }

  useEffect(() => {
    doValidate(inputs);
  }, [inputs])


  const Continue = (e) => {
    e.preventDefault();
    if(doValidate(inputs)) 
       HISTORY.push({ pathname: "/checkout", state: inputs });
  };


  const setError = (inputName, message = null) => {
    const isExist = (
      (
        errors.filter(error=> error.inputName===inputName)
      ) || []
    ).length > 0;
    if(isExist) return;
    setErrors([
      ...errors,
      {
        inputName,
        message,
      },
    ])
  };

  const removeError = (inputNames) => {
    if (!(typeof inputNames === "string" || Array.isArray(inputNames))) return;
    if (!inputNames.length) return;
    if (typeof inputNames === "string") inputNames = [inputNames];
    const newState = errors.filter(
      (error) => !inputNames.includes(error.inputName)
    );
    setErrors(newState)
  };

  const GetError = (props) => {
    const {inputName} = props;
    const EMPTY_COMPONENT = <></>;
    if (!inputName) return EMPTY_COMPONENT;
    const findedError = errors.find(
      (error) => error.inputName === inputName
    );
    if (!findedError) return EMPTY_COMPONENT;
    return (
      <div type="invalid" className="error">
        {findedError.message}
      </div>
    );
  };

  const handleChange = (inputName) => (e) => {
    e.preventDefault();

    setInputs({
      ...inputs,
      [inputName]: e.target.value,
    });
  };


  return (
    <Form className="instantquote p-3 mb-5" novalidateQuote>
      <Form.Group controlId="formGroupFrom">
        <Form.Label className="form-subtitle">Transport car FROM</Form.Label>
        <Form.Control
          type="text"
          placeholder="ZIP or CITY"
          name="zipFrom"
          onChange={handleChange("zipFrom")}
          defaultValue={inputs.zipFrom}
        />
        <GetError inputName={"zipFrom"}></GetError>
      </Form.Group>
      <Form.Group controlId="formGroupTo">
        <Form.Label className="form-subtitle">Transport car TO</Form.Label>
        <Form.Control
          type="text"
          placeholder="ZIP or CITY"
          name="zipTo"
          onChange={handleChange("zipTo")}
          defaultValue={inputs.zipTo}
        />
        <GetError inputName={"zipTo"}></GetError>
      </Form.Group>
      <Form.Group className="radio">
        <Form.Label className="radio-subtitle">Transport type</Form.Label>
        <div
          className="radio-check"
          onChange={handleChange("transportTye")}
        >
          <Form.Check
            type="radio"
            label="Door-to-door"
            id="dooor"
            name="transportType"
            value="Door-to-door"
          />
          <Form.Check
            type="radio"
            label="Open"
            id="open"
            name="transportType"
            value="Open"
          />
          <Form.Check
            type="radio"
            label="Enclosed"
            id="enclosed"
            name="transportType"
            value="Enclosed"
          />
          <Form.Check
            type="radio"
            label="Expired"
            id="expired"
            name="transportType"
            value="Expired"
          />
          <Form.Check
            type="radio"
            label="Hawaii"
            id="hawai"
            name="transportType"
            value="Hawaii"
          />
        </div>
      </Form.Group>
      <button className="btn button" onClick={Continue}>
        Vehicle Details
        <BsArrowRightShort />
      </button>
    </Form>
  );
}

export default InstantQuote;
